
public class ForLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        for(int i=1;i<10;i++) {
          System.out.println(i);
        }
        for(int a=20;a<1000;a+=2) {
        	System.out.println(a);
        }
        System.out.println("loop is over, babe");
	}

}
